    def test_bhd(self):
        driver = self.driver
        driver.get("https://www.bhdleon.com.do")
        driver.switch_to.frame(0)
        driver.switch_to.default_content()
        driver.find_element(By.XPATH, "//*[@id='footer']/section[2]/div/ul/li[5]/a").click()

        bhd_dolar_buy = driver.find_element(By.XPATH, '//*[@id="TasasDeCambio"]/table/tbody/tr[2]/td[2]').text
        bhd_dolar_sell = driver.find_element(By.XPATH, '//*[@id="TasasDeCambio"]/table/tbody/tr[2]/td[3]').text
        bhd_euro_buy = driver.find_element(By.XPATH, '//*[@id="TasasDeCambio"]/table/tbody/tr[3]/td[2]').text
        bhd_euro_sell = driver.find_element(By.XPATH, '//*[@id="TasasDeCambio"]/table/tbody/tr[3]/td[3]').text
        print(f"bhd_dolar_buy: {bhd_dolar_buy}")
        print(f"bhd_dolar_sell: {bhd_dolar_sell}")
        print(f"bhd_euro_buy: {bhd_euro_buy}")
        print(f"bhd_euro_sell: {bhd_euro_sell}")
        driver.close()

