    def test_asocpopular(self):
        driver = self.driver
        driver.get("https://www.apap.com.do/")
        driver.set_window_size(1296, 738)

        asoc_popular_dolar_sell = driver.find_element(By.ID, "currency-sell").text
        asoc_popular_dolar_buy = driver.find_element(By.ID, "currency-buy").text
        driver.find_element(By.ID, "currency-label").click()
        driver.find_element(By.LINK_TEXT, "EUR").click()
        asoc_popular_euro_sell = driver.find_element(By.ID, "currency-sell").text
        asoc_popular_euro_buy = driver.find_element(By.ID, "currency-buy").text

